import React from "react";
import { BrowserRouter, Route, Switch, Redirect } from "react-router-dom";
import { usuario_encontrado } from "./auth.js";
import { render } from "./Componentes/Autenticador";


const Rota_Privada = ({ component: Component, ... rest}) => (
    <Route 
        {...rest} 
            render={props =>(
                usuario_encontrado() ? (                         // Se o usuário estiver logado, damos a ele os componentes necessários encontrados em props
                    <Component {...props} />
                ) : (                                           // Caso não esteja, o enviamos de volta para a tela inicial, com todo o histórico salvo
                    <Redirect to={{ pathname: '/', state: { from: props.location}}}/>
                )
            )
        }
    />
);

const Routes = () => (
    <BrowserRouter>
        <Switch>
            <Route exact path="/" component={ () => (this.render()
                    /*<div className = "Autenticador">
                        <h2>Github Search</h2>
                        <input type="text" onChange={(event)=>{this.setState({github_login:event.target.value})}} ></input>
                    </div>*/
            )}/>
            <Rota_Privada path="/app" component={() => <h1>Você está logado!</h1>}/>
        </Switch>
    </BrowserRouter>

);

export default Routes;
