import React, { Component } from 'react'
import './DetailScreen.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'

/*  
O localStorage contém:
    @Trinity/nome
    @Trinity/usuario
    @Trinity/repositories
    @Trinity/followers
    @Trinity/location
    @Trinity/message
    @Trinity/repo{x} onde x é o número de repositórios, de acordo com a quantidade de repositorios em @trinity/repositories

*/



export default class DetailScreen extends Component {

    constructor(props){
        super(props);
        let nome;
        let user;
        let repos;


        this.state = {
            nome            : localStorage.getItem('@Trinity/nome'),
            user            : localStorage.getItem('@Trinity/usuario'),
            repos           : localStorage.getItem('@Trinity/repositories'),
            followers       : localStorage.getItem('@Trinity/followers'),
            location        : localStorage.getItem('@Trinity/location'),
            message         : localStorage.getItem('@Trinity/message'),
            repositories    : new Array(repos)
        }

                console.log("Nome: " + this.state.nome);
                console.log("Usuário: " + this.state.user);

    }


    render() {
        return (
            <div className = 'detalhes'>
                        <title className = 'titulo_pag'> Usuário </title>
                        <div className = 'nome'>
                            <h3 className = 'Titulo'>
                                {this.state.nome}
                            </h3>
                            <p>
                                {this.state.repos}
                            </p>
                            <p>
                                {this.state.followers}
                            </p>
                        </div>
                        <div className = 'roda_braco'>
                            <h1>
                                Github
                            </h1>
                            <h2>
                                Search
                            </h2>
                            <input className = "user_input" type = "text" placeholder = 'type your git user' onChange = {(event)=>{this.setState({github_login:event.target.value})}} />
                                <button className = 'SearchButton'  onClick = {this.autenticar} > 
                                <FontAwesomeIcon icon   = {faSearch} className  = 'search-btn'/>
                                </button>
                        </div>
            </div>

        )
    }
}
